import axios from 'axios';
import { Task } from '../interfaces/Task';

const API_BASE_URL = 'https://localhost:7244/api'; // Adjust if your API base URL is different

const api = axios.create({
  baseURL: API_BASE_URL,
});

export const getTasks = () => {
  return api.get('/tasks').then(response => response.data);
};

export const createTask = (task: Task) => {
  return api.post('/tasks', task).then(response => response.data);
};

export const updateTask = (task: Task) => {
  return api.put(`/tasks/${task.id}`, task).then(response => response.data);
};

export const deleteTask = (taskId: number) => {
  return api.delete(`/tasks/${taskId}`).then(response => response.data);
};

export default {
  getTasks,
  createTask,
  updateTask,
  deleteTask,
};
