import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { RootState } from './store';
import { loadUser } from './slices/userSlice';
import { fetchTasksByUserId } from './slices/taskSlice';
import './assets/css/styles.css'; // Correct path
import Header from './components/Header';
import TaskList from './components/TaskList';
import AddTaskForm from './components/AddTaskForm';
import UserProfilePopup from './components/UserProfilePopup';

const App: React.FC = () => {
  const dispatch = useDispatch();
  const { user, tasks, showAddTaskForm, showProfilePopup } = useSelector((state: RootState) => ({
    user: state.user.user,
    tasks: state.tasks.tasks,
    showAddTaskForm: state.ui.showAddTaskForm,
    showProfilePopup: state.ui.showProfilePopup,
  }));

  useEffect(() => {
    dispatch(loadUser());
  }, [dispatch]);

  useEffect(() => {
    if (user) {
      dispatch(fetchTasksByUserId(user.id));
    }
  }, [user, dispatch]);

  return (
    <div className="app-container">
      <Header />
      <TaskList tasks={tasks} />
      {showAddTaskForm && <AddTaskForm />}
      {showProfilePopup && (
        <UserProfilePopup
          user={user}
          open={showProfilePopup}
          onClose={() => dispatch(toggleProfilePopup())}
          onUpdateProfilePicture={() => dispatch(loadUser())}
          onDeleteProfilePicture={() => dispatch(loadUser())}
        />
      )}
    </div>
  );
};

export default App;
