import React, { useState, useEffect } from 'react';
import { TextField, Button, Box, MenuItem } from '@mui/material';
import taskService from '../services/taskService';
import { Task } from '../interfaces/Task';
import '../forms.css'; // Import the CSS file

interface AddTaskFormProps {
  onTaskAdded: () => void;
  onClose: () => void;
  editingTask: Task | null;
}

const AddTaskForm: React.FC<AddTaskFormProps> = ({ onTaskAdded, onClose, editingTask }) => {
  const [title, setTitle] = useState(editingTask ? editingTask.title : '');
  const [description, setDescription] = useState(editingTask ? editingTask.description : '');
  const [status, setStatus] = useState<'Pending' | 'InProgress' | 'Completed'>(editingTask ? editingTask.status : 'Pending');

  useEffect(() => {
    if (editingTask) {
      setTitle(editingTask.title);
      setDescription(editingTask.description);
      setStatus(editingTask.status);
    }
  }, [editingTask]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const newTask: Task = {
      id: editingTask ? editingTask.id : 0, // If editing, use the existing ID
      title,
      description,
      status,
      userId: editingTask ? editingTask.userId : 0 // Adjust this based on your logic for user ID
    };

    try {
      if (editingTask) {
        await taskService.updateTask(newTask);
      } else {
        await taskService.createTask(newTask);
      }
      onTaskAdded();
      onClose();
    } catch (error) {
      console.error('Failed to save task:', error);
    }
  };

  return (
    <Box className="form-container">
      <form onSubmit={handleSubmit}>
        <TextField
          label="Title"
          variant="outlined"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="form-field"
          required
        />
        <TextField
          label="Description"
          variant="outlined"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="form-field"
          required
        />
        <TextField
          select
          label="Status"
          variant="outlined"
          value={status}
          onChange={(e) => setStatus(e.target.value as 'Pending' | 'InProgress' | 'Completed')}
          className="form-field"
          required
        >
          <MenuItem value="Pending">Pending</MenuItem>
          <MenuItem value="InProgress">In Progress</MenuItem>
          <MenuItem value="Completed">Completed</MenuItem>
        </TextField>
        <Button
          variant="contained"
          color="primary"
          type="submit"
          className="form-button"
        >
          {editingTask ? 'Update Task' : 'Add Task'}
        </Button>
        <Button
          variant="outlined"
          color="secondary"
          onClick={onClose}
          className="form-button"
        >
          Cancel
        </Button>
      </form>
    </Box>
  );
};

export default AddTaskForm;
