import React, { useState } from 'react';
import taskService from '../services/taskService';
import { Task } from '../interfaces/Task';
import '../styles.css';

interface TaskListProps {
    tasks: Task[];
    loadTasks: () => void;
}

const TaskList: React.FC<TaskListProps> = ({ tasks, loadTasks }) => {
    const [editingTaskId, setEditingTaskId] = useState<number | null>(null);
    const [editingTitle, setEditingTitle] = useState<string>('');
    const [editingDescription, setEditingDescription] = useState<string>('');

    const deleteTask = async (id: number) => {
        try {
            await taskService.deleteTask(id);
            loadTasks();
        } catch (error) {
            console.error(`Failed to delete task with id ${id}:`, error);
        }
    };

    const handleEdit = (task: Task) => {
        setEditingTaskId(task.id!);
        setEditingTitle(task.title);
        setEditingDescription(task.description);
    };

    const handleUpdate = async () => {
        if (editingTaskId === null) return;
        try {
            const updatedTask: Task = {
                id: editingTaskId,
                title: editingTitle,
                description: editingDescription,
                status: tasks.find(task => task.id === editingTaskId)?.status as "Pending" | "InProgress" | "Completed",
            };
            await taskService.updateTask(editingTaskId, updatedTask);
            loadTasks();
            setEditingTaskId(null);
        } catch (error) {
            console.error(`Failed to update task with id ${editingTaskId}:`, error);
        }
    };

    const handleStatusChange = async (task: Task, newStatus: "Pending" | "InProgress" | "Completed") => {
        try {
            const updatedTask = { ...task, status: newStatus };
            await taskService.updateTask(task.id!, updatedTask);
            loadTasks();
        } catch (error) {
            console.error(`Failed to update task status with id ${task.id}:`, error);
        }
    };

    const handleBlur = (e: React.FocusEvent<HTMLDivElement>) => {
        if (!e.currentTarget.contains(e.relatedTarget)) {
            handleUpdate();
        }
    };

    return (
        <div>
            <h1>Task List</h1>
            <ul className="task-list">
                {tasks.map(task => (
                    <li
                        key={task.id}
                        className={`task-item ${task.status === 'Completed' ? 'task-done' : 'task-not-done'}`}
                    >
                        <div className="task-info" onBlur={handleBlur} tabIndex={-1}>
                            {editingTaskId === task.id ? (
                                <>
                                    <input
                                        type="text"
                                        value={editingTitle}
                                        onChange={(e) => setEditingTitle(e.target.value)}
                                        className="form-control"
                                        autoFocus
                                        placeholder="Title"
                                    />
                                    <textarea
                                        value={editingDescription}
                                        onChange={(e) => setEditingDescription(e.target.value)}
                                        className="form-control"
                                        placeholder="Description"
                                    />
                                </>
                            ) : (
                                <>
                                    <div className="task-title" onClick={() => handleEdit(task)}>
                                        {task.title}
                                    </div>
                                    <div className="task-description" onClick={() => handleEdit(task)}>
                                        {task.description}
                                    </div>
                                </>
                            )}
                        </div>
                        <div className="task-actions">
                            <button
                                className="btn btn-toggle"
                                onClick={() => handleStatusChange(task, task.status === 'Pending' ? 'InProgress' : task.status === 'InProgress' ? 'Completed' : 'Pending')}
                            >
                                {task.status === 'Pending' ? 'In Progress' : task.status === 'InProgress' ? 'Completed' : 'Pending'}
                            </button>
                            <button
                                className="btn btn-delete"
                                onClick={() => deleteTask(task.id!)}
                            >
                                Delete
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TaskList;
