import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import TaskList from './components/TaskList';
import AddTaskForm from './components/AddTaskForm';
import RegisterForm from './components/RegisterForm';
import Login from './components/Login';
import UserProfilePopup from './components/UserProfilePopup';
import taskService from './services/taskService';
import userService from './services/userService';
import { Task } from './interfaces/Task';
import { User } from './interfaces/User';
import { Button, Container, AppBar, Toolbar, Typography, Box, Avatar, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';

const App: React.FC = () => {
    const [tasks, setTasks] = useState<Task[]>([]);
    const [user, setUser] = useState<User | null>(null);
    const [showAddTaskForm, setShowAddTaskForm] = useState<boolean>(false);
    const [openProfilePopup, setOpenProfilePopup] = useState<boolean>(false);

    useEffect(() => {
        loadTasks();
        loadUser();
    }, []);

    const loadTasks = async () => {
        try {
            const tasksData = await taskService.getTasks();
            setTasks(tasksData);
        } catch (error) {
            console.error('Failed to load tasks:', error);
        }
    };

    const loadUser = async () => {
        try {
            const userData = await userService.getUser();
            setUser(userData);
        } catch (error) {
            console.error('Failed to load user:', error);
        }
    };

    const countTasksByStatus = (status: string) => {
        return tasks.filter(task => task.status === status).length;
    };

    const handleAddTaskClick = () => {
        setShowAddTaskForm(!showAddTaskForm);
    };

    const handleRegister = (user: User) => {
        setUser(user);
    };

    const handleLogin = (user: User) => {
        setUser(user);
    };

    const handleUpdateProfilePicture = async (file: File) => {
        try {
            await userService.updateProfilePicture(file);
            await loadUser();
        } catch (error) {
            console.error('Error updating profile picture:', error);
        }
    };

    const handleDeleteProfilePicture = async () => {
        try {
            await userService.deleteProfilePicture();
            await loadUser();
        } catch (error) {
            console.error('Error deleting profile picture:', error);
        }
    };

    const handleProfileClick = () => {
        setOpenProfilePopup(true);
    };

    const handleCloseProfilePopup = () => {
        setOpenProfilePopup(false);
    };

    return (
        <Router>
            <div className="professional-background left"></div>
            <div className="professional-background right"></div>
            <Container maxWidth="md" className="content-container">
                <AppBar position="static">
                    <Toolbar>
                        <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                            Task Management App
                        </Typography>
                        <Box display="flex" alignItems="center">
                            {user ? (
                                <>
                                    <Button onClick={handleProfileClick} sx={{ textTransform: 'none' }}>
                                        <Avatar alt={user.name || 'User'} src={user.profilePicture || ''} />
                                    </Button>
                                    <Button color="inherit" onClick={() => setUser(null)}>Logout</Button>
                                </>
                            ) : (
                                <>
                                    <Button color="inherit" component={Link} to="/register">Register</Button>
                                    <Button color="inherit" component={Link} to="/login">Login</Button>
                                </>
                            )}
                        </Box>
                    </Toolbar>
                </AppBar>
                <main>
                    <Box className="task-counters" sx={{ marginTop: 2, padding: 2, backgroundColor: '#f8f9fa', borderRadius: 2 }}>
                        <Typography variant="h5" gutterBottom>Task Status</Typography>
                        <Box display="flex" justifyContent="center">
                            <Box display="flex" alignItems="center" mx={2}>
                                <span className="status-key pending"></span>
                                <Typography variant="body1" sx={{ marginLeft: 1 }}>Pending: {countTasksByStatus('Pending')}</Typography>
                            </Box>
                            <Box display="flex" alignItems="center" mx={2}>
                                <span className="status-key in-progress"></span>
                                <Typography variant="body1" sx={{ marginLeft: 1 }}>In Progress: {countTasksByStatus('InProgress')}</Typography>
                            </Box>
                            <Box display="flex" alignItems="center" mx={2}>
                                <span className="status-key completed"></span>
                                <Typography variant="body1" sx={{ marginLeft: 1 }}>Completed: {countTasksByStatus('Completed')}</Typography>
                            </Box>
                        </Box>
                    </Box>
                    <Box display="flex" justifyContent="center" sx={{ marginTop: 2 }}>
                        <Button variant="contained" color="primary" onClick={handleAddTaskClick}>
                            {showAddTaskForm ? 'Hide Form' : 'Add New Task'}
                        </Button>
                    </Box>
                    {showAddTaskForm && <AddTaskForm onTaskAdded={loadTasks} onClose={() => setShowAddTaskForm(false)} />}
                    <TaskList tasks={tasks} loadTasks={loadTasks} />
                    {user && (
                        <UserProfilePopup
                            user={user}
                            open={openProfilePopup}
                            onClose={handleCloseProfilePopup}
                            onUpdateProfilePicture={handleUpdateProfilePicture}
                            onDeleteProfilePicture={handleDeleteProfilePicture}
                        />
                    )}
                    <Switch>
                        <Route path="/register">
                            <Dialog open={true} onClose={() => window.history.back()}>
                                <DialogTitle>Register</DialogTitle>
                                <DialogContent>
                                    <RegisterForm onRegister={handleRegister} />
                                </DialogContent>
                                <DialogActions>
                                    <Button onClick={() => window.history.back()} color="primary">Close</Button>
                                </DialogActions>
                            </Dialog>
                        </Route>
                        <Route path="/login">
                            <Dialog open={true} onClose={() => window.history.back()}>
                                <DialogTitle>Login</DialogTitle>
                                <DialogContent>
                                    <Login onLogin={handleLogin} />
                                </DialogContent>
                                <DialogActions>
                                    <Button onClick={() => window.history.back()} color="primary">Close</Button>
                                </DialogActions>
                            </Dialog>
                        </Route>
                    </Switch>
                </main>
            </Container>
        </Router>
    );
};

export default App;
