import React, { useState } from 'react';
import taskService from '../services/taskService';
import { Task } from '../interfaces/Task';
import '../styles.css';

interface AddTaskFormProps {
    onTaskAdded: () => void;
    onClose: () => void;
}

const AddTaskForm: React.FC<AddTaskFormProps> = ({ onTaskAdded, onClose }) => {
    const [title, setTitle] = useState<string>('');
    const [description, setDescription] = useState<string>('');

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        try {
            const newTask: Task = {
                title,
                description,
                status: 'Pending' // Default status
            };

            await taskService.createTask(newTask);
            onTaskAdded();
            setTitle('');
            setDescription('');
            onClose();
        } catch (error) {
            console.error('Error adding task:', error);
        }
    };

    return (
        <form className="my-4 custom-form" onSubmit={handleSubmit}>
            <h2>Add New Task</h2>
            <div className="mb-3">
                <label className="form-label">Title:</label>
                <input
                    type="text"
                    className="form-control"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                    placeholder="Title" // Placeholder for title input
                />
            </div>
            <div className="mb-3">
                <label className="form-label">Description:</label>
                <textarea
                    className="form-control"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    required
                    placeholder="Description" // Placeholder for description input
                />
            </div>
            <button type="submit" className="btn btn-primary">Add Task</button>
            <button type="button" className="btn btn-secondary" onClick={onClose}>Cancel</button>
        </form>
    );
};

export default AddTaskForm;
