import axios from 'axios';
import { User } from '../interfaces/User'; // Ensure this is imported

const API_BASE_URL = 'https://localhost:7244/api'; // Update the port number if different

const api = axios.create({
  baseURL: API_BASE_URL,
});

export interface LoginData {
  username: string;
  passwordHash: string;
}

export const registerUser = (userData: Partial<User>) => { // Partial to handle optional fields
  return api.post('/User/register', userData);
};

export const loginUser = (loginData: LoginData) => {
  return api.post('/User/login', loginData);
};

export default api;
