import axios from 'axios';

const API_BASE_URL = 'https://localhost:7244/api';

export const registerUser = async (user: any) => {
    const response = await axios.post(`${API_BASE_URL}/users/register`, user);
    return response.data;
};

export const loginUser = async (credentials: { username: string; passwordHash: string }) => {
    const response = await axios.post(`${API_BASE_URL}/users/login`, credentials);
    return response.data;
};

export const getCurrentUser = async () => {
    const response = await axios.get(`${API_BASE_URL}/users/current-user`);
    return response.data;
};

export const getTasksByUserId = async (userId: number) => {
    const response = await axios.get(`${API_BASE_URL}/Tasks/user/${userId}`);
    return response.data;
};
