import axios from 'axios';

const API_BASE_URL = 'https://localhost:7244/api'; // Ensure this matches your backend configuration

const api = axios.create({
  baseURL: API_BASE_URL,
});

export interface User {
  username: string;
  email: string;
  passwordHash: string;
  profilePictureFileName?: string;
}

export interface LoginData {
  username: string;
  passwordHash: string;
}

export const registerUser = (userData: Partial<User>) => {
  return api.post('/User/register', userData);
};

export const loginUser = (loginData: LoginData) => {
  return api.post('/User/login', loginData);
};

export const getUser = () => {
  return api.get('/users/current-user');
};

export const updateProfilePicture = (file: File) => {
  const formData = new FormData();
  formData.append('file', file);
  return api.post('/User/uploadProfilePicture', formData);
};

export const deleteProfilePicture = () => {
  return api.delete('/User/deleteProfilePicture');
};
