const handleUpdateProfilePicture = async () => {
  if (selectedFile) {
      try {
          const formData = new FormData();
          formData.append('profilePicture', selectedFile);
          formData.append('userId', user.id.toString());

          const response = await axios.put(`${API_URL}/profilePicture`, formData, {
              headers: {
                  'Content-Type': 'multipart/form-data',
              },
          });

          if (response.status === 200) {
              onUpdateProfilePicture(); // Notify parent component of the update
              setSelectedFile(null);
              onClose();
          }
      } catch (error: any) {
          if (error.response && error.response.status === 400 && error.response.data === "The uploaded file is the same as the existing one.") {
              // Handle specific response when the same file is uploaded
              console.log("The uploaded file is the same as the existing one.");
              setSelectedFile(null);
              onClose();
          } else {
              console.error('Failed to update profile picture:', error);
          }
      }
  }
};
