import React from 'react';
import { Task } from '../interfaces/Task';
import { Box, List, ListItem, ListItemText, IconButton, Button } from '@mui/material';
import taskService from '../services/taskService';
import EditTaskForm from './EditTaskForm';

interface TaskListProps {
    tasks: Task[];
    loadTasks: () => void;
}

const TaskList: React.FC<TaskListProps> = ({ tasks, loadTasks }) => {
    const [editingTask, setEditingTask] = React.useState<Task | null>(null);

    const handleDeleteTask = async (taskId: number) => {
        try {
            await taskService.deleteTask(taskId);
            loadTasks();
        } catch (error) {
            console.error('Failed to delete task:', error);
        }
    };

    const handleEditTask = (task: Task) => {
        setEditingTask(task);
    };

    const handleUpdateTask = async (updatedTask: Task) => {
        try {
            await taskService.updateTask(updatedTask.id, updatedTask);
            setEditingTask(null);
            loadTasks();
        } catch (error) {
            console.error('Failed to update task:', error);
        }
    };

    return (
        <List>
            {tasks.map(task => (
                <ListItem key={task.id} className="task-item">
                    {editingTask && editingTask.id === task.id ? (
                        <EditTaskForm task={task} onUpdate={handleUpdateTask} onCancel={() => setEditingTask(null)} />
                    ) : (
                        <>
                            <ListItemText
                                primary={
                                    <Box display="flex" alignItems="center">
                                        <span className={`status-key ${task.status.toLowerCase()}`} />
                                        {task.title}
                                    </Box>
                                }
                                secondary={task.description}
                            />
                            <Box className="task-actions">
                                <Button onClick={() => handleEditTask(task)} sx={{ textTransform: 'none' }}>Edit</Button>
                                <Button onClick={() => handleDeleteTask(task.id)} sx={{ textTransform: 'none' }}>Delete</Button>
                            </Box>
                        </>
                    )}
                </ListItem>
            ))}
        </List>
    );
};

export default TaskList;
