import React from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Box, Avatar } from '@mui/material';

interface UserProfilePopupProps {
    user: { name: string, profilePicture: string };
    open: boolean;
    onClose: () => void;
    onUpdateProfilePicture: (file: File) => void;
    onDeleteProfilePicture: () => void;
}

const UserProfilePopup: React.FC<UserProfilePopupProps> = ({
    user, open, onClose, onUpdateProfilePicture, onDeleteProfilePicture
}) => {
    return (
        <Dialog open={open} onClose={onClose}>
            <DialogTitle>Profile Picture Options</DialogTitle>
            <DialogContent>
                <Box display="flex" flexDirection="column" alignItems="center">
                    <Avatar alt={user.name} src={user.profilePicture} sx={{ width: 100, height: 100, mb: 1 }} />
                    <Button variant="contained" color="primary" component="label">
                        Update Profile Picture
                        <input type="file" hidden onChange={(e) => onUpdateProfilePicture(e.target.files![0])} accept="image/*" />
                    </Button>
                    <Button variant="outlined" color="secondary" onClick={onDeleteProfilePicture} sx={{ mt: 1 }}>
                        Delete Profile Picture
                    </Button>
                </Box>
            </DialogContent>
            <DialogActions>
                <Button onClick={onClose} color="primary">Close</Button>
            </DialogActions>
        </Dialog>
    );
};

export default UserProfilePopup;
