import React, { useState } from 'react';
import { loginUser } from '../services/api';
import { User } from '../interfaces/User';
import { TextField, Button, Typography, Box } from '@mui/material';

interface LoginProps {
    onLogin: (user: User) => void;
}

const Login: React.FC<LoginProps> = ({ onLogin }) => {
    const [email, setEmail] = useState<string>('');
    const [password, setPassword] = useState<string>('');
    const [message, setMessage] = useState<string>('');
    const [isError, setIsError] = useState<boolean>(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const loginData = { username: email, passwordHash: password };
        try {
            const response = await loginUser(loginData);
            onLogin(response); // Pass the user data to the parent component
            setMessage('Login successful!');
            setIsError(false);
        } catch (error) {
            setMessage('Login failed. Please check your credentials.');
            setIsError(true);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ marginTop: 4 }}>
            <Typography variant="h6">Login</Typography>
            <TextField
                label="Email"
                variant="outlined"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Password"
                variant="outlined"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            {message && (
                <Typography variant="body1" color={isError ? 'error' : 'primary'} sx={{ marginBottom: 2 }}>
                    {message}
                </Typography>
            )}
            <Button type="submit" variant="contained" color="primary" sx={{ marginRight: 1 }}>Login</Button>
        </Box>
    );
};

export default Login;
