import axios from 'axios';
import { User } from '../interfaces/User';

const API_URL = 'http://localhost:5000/api/users';

const getUser = async (): Promise<User> => {
    try {
        const response = await axios.get(`${API_URL}/current-user`);
        return response.data;
    } catch (error) {
        throw error;
    }
};

const updateProfilePicture = async (file: File): Promise<void> => {
    try {
        const formData = new FormData();
        formData.append('file', file);
        await axios.post(`${API_URL}/current-user/upload-profile-picture`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data',
            },
        });
    } catch (error) {
        throw error;
    }
};

const deleteProfilePicture = async (): Promise<void> => {
    try {
        await axios.delete(`${API_URL}/current-user/remove-profile-picture`);
    } catch (error) {
        throw error;
    }
};

const register = async (user: User): Promise<User> => {
    try {
        const response = await axios.post(API_URL, user);
        return response.data;
    } catch (error) {
        throw error;
    }
};

export default {
    getUser,
    updateProfilePicture,
    deleteProfilePicture,
    register,
};
