import api from './api';
import { User } from '../interfaces/User';

const getUser = async (): Promise<User> => {
    const response = await api.get('/users/current-user');
    return response.data;
};

const getCurrentUser = async (): Promise<User> => {
    const response = await api.get('/users/current-user');
    return response.data;
};

const updateProfilePicture = async (file: File): Promise<void> => {
    const formData = new FormData();
    formData.append('file', file);
    await api.post('/users/profile-picture', formData);
};

const deleteProfilePicture = async (): Promise<void> => {
    await api.delete('/users/profile-picture');
};

const register = async (user: User): Promise<User> => {
    const response = await api.post('/users/register', user);
    return response.data;
};

const login = async (credentials: { email: string; passwordHash: string }): Promise<User> => {
    const response = await api.post('/users/login', credentials);
    return response.data;
};

export default { getUser, getCurrentUser, updateProfilePicture, deleteProfilePicture, register, login };
