import React, { useState } from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Avatar, Box, Typography } from '@mui/material';
import { User } from '../interfaces/User';
import userService from '../services/userService';

interface UserProfilePopupProps {
  user: User;
  open: boolean;
  onClose: () => void;
  onUpdateProfilePicture: () => void;
  onDeleteProfilePicture: () => void;
}

const UserProfilePopup: React.FC<UserProfilePopupProps> = ({ user, open, onClose, onUpdateProfilePicture, onDeleteProfilePicture }) => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files.length > 0) {
      setSelectedFile(event.target.files[0]);
    }
  };

  const handleUploadClick = async () => {
    if (selectedFile) {
      try {
        await userService.updateProfilePicture(selectedFile);
        onUpdateProfilePicture();
        setSelectedFile(null);
        onClose();
      } catch (error) {
        console.error('Failed to update profile picture:', error);
      }
    }
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Profile</DialogTitle>
      <DialogContent>
        <Box display="flex" flexDirection="column" alignItems="center">
          <Avatar alt={user.username} src={user.profilePictureFileName || ''} sx={{ width: 80, height: 80 }} />
          <Typography variant="h6">{user.username}</Typography>
          <Typography variant="body2">{user.email}</Typography>
          <input type="file" accept="image/*" onChange={handleFileChange} />
          {selectedFile && (
            <Button variant="contained" color="primary" onClick={handleUploadClick}>
              Upload
            </Button>
          )}
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="primary">Close</Button>
        <Button onClick={onDeleteProfilePicture} color="secondary">Delete Profile Picture</Button>
      </DialogActions>
    </Dialog>
  );
};

export default UserProfilePopup;
