import React, { useState } from 'react';
import { TextField, Button, Box, Select, MenuItem, FormControl, InputLabel } from '@mui/material';
import { Task } from '../interfaces/Task';
import taskService from '../services/taskService';

interface AddTaskFormProps {
    onTaskAdded: () => Promise<void>;
    onClose: () => void;
    editingTask?: Task; // Make editingTask optional
}

const AddTaskForm: React.FC<AddTaskFormProps> = ({ onTaskAdded, onClose, editingTask }) => {
    const [title, setTitle] = useState<string>(editingTask?.title || '');
    const [description, setDescription] = useState<string>(editingTask?.description || '');
    const [status, setStatus] = useState<'Pending' | 'Completed' | 'InProgress'>(editingTask?.status || 'Pending');

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const newTask: Task = {
            id: editingTask?.id || 0,
            title,
            description,
            status,
            userId: editingTask?.userId || 0, // Adjust this as needed for your application
        };
        try {
            if (editingTask) {
                await taskService.updateTask(newTask);
            } else {
                await taskService.createTask(newTask);
            }
            await onTaskAdded();
            onClose();
        } catch (error) {
            console.error('Failed to save task:', error);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ marginTop: 4 }}>
            <TextField
                label="Title"
                variant="outlined"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Description"
                variant="outlined"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
                multiline
                rows={4}
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <FormControl fullWidth sx={{ marginBottom: 2 }}>
                <InputLabel>Status</InputLabel>
                <Select
                    value={status}
                    onChange={(e) => setStatus(e.target.value as 'Pending' | 'Completed' | 'InProgress')}
                    label="Status"
                >
                    <MenuItem value="Pending">Pending</MenuItem>
                    <MenuItem value="InProgress">In Progress</MenuItem>
                    <MenuItem value="Completed">Completed</MenuItem>
                </Select>
            </FormControl>
            <Button type="submit" variant="contained" color="primary" sx={{ marginRight: 1 }}>
                {editingTask ? 'Update Task' : 'Add Task'}
            </Button>
            <Button type="button" variant="outlined" color="secondary" onClick={onClose}>
                Cancel
            </Button>
        </Box>
    );
};

export default AddTaskForm;
