import axios from 'axios';
import { User } from '../interfaces/User';
import { getCurrentUser, registerUser, loginUser } from './api';

const getUser = async (): Promise<User> => {
    return await getCurrentUser();
};

const updateProfilePicture = async (file: File): Promise<void> => {
    const formData = new FormData();
    formData.append('file', file);
    await axios.post('/api/users/profile-picture', formData);
};

const deleteProfilePicture = async (): Promise<void> => {
    await axios.delete('/api/users/profile-picture');
};

const register = async (user: User): Promise<User> => {
    return await registerUser(user);
};

const login = async (user: User): Promise<User> => {
    return await loginUser(user);
};

export default {
    getUser,
    updateProfilePicture,
    deleteProfilePicture,
    register,
    login,
};
