import React, { useState } from 'react';
import taskService from '../services/taskService';
import { Task } from '../interfaces/Task';
import { TextField, Button, Box, Typography } from '@mui/material';

interface AddTaskFormProps {
    onTaskAdded: () => void;
    onClose: () => void;
}

const AddTaskForm: React.FC<AddTaskFormProps> = ({ onTaskAdded, onClose }) => {
    const [title, setTitle] = useState<string>('');
    const [description, setDescription] = useState<string>('');

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        try {
            const newTask: Task = {
                title,
                description,
                status: 'Pending'
            };

            await taskService.createTask(newTask);
            onTaskAdded();
            setTitle('');
            setDescription('');
            onClose();
        } catch (error) {
            console.error('Error adding task:', error);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} className="custom-form">
            <Typography variant="h6">Add New Task</Typography>
            <TextField
                label="Title"
                variant="outlined"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Description"
                variant="outlined"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
                multiline
                rows={4}
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <Box display="flex" justifyContent="space-between">
                <Button type="submit" variant="contained" color="primary" sx={{ marginRight: 1 }}>Add Task</Button>
                <Button type="button" variant="outlined" color="secondary" onClick={onClose}>Cancel</Button>
            </Box>
        </Box>
    );
};

export default AddTaskForm;
