import React, { useState } from 'react';
import { TextField, Button, Box, Typography } from '@mui/material';
import { loginUser } from '../services/api';
import { User } from '../interfaces/User';

interface LoginProps {
    onLogin: (user: User) => void;
}

const Login: React.FC<LoginProps> = ({ onLogin }) => {
    const [username, setUsername] = useState<string>('');
    const [password, setPassword] = useState<string>('');
    const [message, setMessage] = useState<string>('');
    const [isError, setIsError] = useState<boolean>(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const loginData = { username, passwordHash: password }; // Correct the fields here
        try {
            const response = await loginUser(loginData);
            onLogin(response); // Pass the user data to the parent component
            setMessage('Login successful!');
            setIsError(false);
        } catch (error) {
            setMessage('Login failed.');
            setIsError(true);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ marginTop: 4 }}>
            <Typography variant="h6">Login</Typography>
            <TextField
                label="Username"
                variant="outlined"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Password"
                variant="outlined"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <Button type="submit" variant="contained" color="primary" sx={{ marginRight: 1 }}>
                Login
            </Button>
            {message && (
                <Typography variant="body1" color={isError ? 'error' : 'primary'}>
                    {message}
                </Typography>
            )}
        </Box>
    );
};

export default Login;
