import React, { useState } from 'react';
import { loginUser } from '../services/api';
import { TextField, Button, Box, Typography } from '@mui/material';
import { User } from '../interfaces/User';  // Import User interface

interface LoginProps {
    onLogin: (user: User) => void;
}

const Login: React.FC<LoginProps> = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [passwordHash, setPasswordHash] = useState('');
    const [message, setMessage] = useState('');
    const [isError, setIsError] = useState(false);

    const handleSubmit = async (event: React.FormEvent) => {
        event.preventDefault();
        const loginData = { email, passwordHash };

        try {
            const response = await loginUser(loginData);
            onLogin(response); // Pass the user data to the parent component
            setMessage('Login successful!');
            setIsError(false);
        } catch (error) {
            setMessage('Login failed. Please check your credentials.');
            setIsError(true);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
            <Typography variant="h6">Login</Typography>
            <TextField
                label="Email"
                variant="outlined"
                fullWidth
                margin="normal"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
            />
            <TextField
                label="Password"
                variant="outlined"
                type="password"
                fullWidth
                margin="normal"
                value={passwordHash}
                onChange={(e) => setPasswordHash(e.target.value)}
            />
            <Button type="submit" variant="contained" color="primary" fullWidth>
                Login
            </Button>
            {message && (
                <Typography variant="body2" color={isError ? 'error' : 'primary'} sx={{ mt: 2 }}>
                    {message}
                </Typography>
            )}
        </Box>
    );
};

export default Login;
