import React, { useState, useEffect } from 'react';
import { TextField, Button, Box } from '@mui/material';
import taskService from '../services/taskService';
import { Task } from '../interfaces/Task';
import '../forms.css'; // Import the CSS file

interface AddTaskFormProps {
    onTaskAdded: () => void;
    onClose: () => void;
    editingTask: Task | null;
}

const AddTaskForm: React.FC<AddTaskFormProps> = ({ onTaskAdded, onClose, editingTask }) => {
    const [title, setTitle] = useState<string>('');
    const [description, setDescription] = useState<string>('');
    const [status, setStatus] = useState<'Pending' | 'InProgress' | 'Completed'>('Pending');

    useEffect(() => {
        if (editingTask) {
            setTitle(editingTask.title);
            setDescription(editingTask.description);
            setStatus(editingTask.status);
        } else {
            setTitle('');
            setDescription('');
            setStatus('Pending');
        }
    }, [editingTask]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            if (editingTask) {
                await taskService.updateTask(editingTask.id, { ...editingTask, title, description, status });
            } else {
                await taskService.createTask({ id: 0, title, description, status, userId: 0 });
            }
            onTaskAdded();
            onClose();
        } catch (error) {
            console.error('Failed to add/update task:', error);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ marginTop: 4 }}>
            <TextField
                label="Title"
                variant="outlined"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Description"
                variant="outlined"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
                multiline
                rows={4}
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <Button type="submit" variant="contained" color="primary" sx={{ marginRight: 1 }}>
                {editingTask ? 'Update Task' : 'Add Task'}
            </Button>
            <Button type="button" variant="outlined" color="secondary" onClick={onClose}>
                Cancel
            </Button>
        </Box>
    );
};

export default AddTaskForm;
