import React from 'react';
import { Task } from '../interfaces/Task';
import taskService from '../services/taskService';

interface TaskListProps {
    tasks: Task[];
    loadTasks: () => void;
}

const TaskList: React.FC<TaskListProps> = ({ tasks, loadTasks }) => {
    const handleDeleteTask = async (taskId: number) => {
        try {
            await taskService.deleteTask(taskId);
            loadTasks();
        } catch (error) {
            console.error('Failed to delete task:', error);
        }
    };

    const handleEditTask = (task: Task) => {
        // Add your edit logic here
    };

    return (
        <ul className="task-list">
            {tasks.map(task => (
                <li key={task.id} className="task-item">
                    <div className="task-info">
                        <span className={`status-key ${task.status.toLowerCase().replace(/\s+/g, '-')}`}></span>
                        <div className="task-title">{task.title}</div>
                        <div className="task-description">{task.description}</div>
                        <div className="task-status">{task.status}</div>
                    </div>
                    <div className="task-actions">
                        <button onClick={() => handleEditTask(task)} className="btn btn-toggle">
                            üñâ
                        </button>
                        <button onClick={() => handleDeleteTask(task.id)} className="btn btn-delete">
                            üóëÔ∏è
                        </button>
                    </div>
                </li>
            ))}
        </ul>
    );
};

export default TaskList;
