import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import TaskList from './components/TaskList';
import AddTaskForm from './components/AddTaskForm';
import RegisterForm from './components/RegisterForm';
import Login from './components/Login';
import UserProfilePopup from './components/UserProfilePopup';
import { RootState } from './store';
import { User } from './interfaces/User';
import { Button, Container, AppBar, Toolbar, Typography, Box, Avatar, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import { fetchTasksByUserId } from './slices/tasksSlice';
import { loadUser, loginUser, logoutUser } from './slices/userSlice';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';

const App: React.FC = () => {
  const dispatch = useDispatch();
  const tasks = useSelector((state: RootState) => state.tasks.tasks);
  const user = useSelector((state: RootState) => state.user.user);
  const [showAddTaskForm, setShowAddTaskForm] = useState<boolean>(false);
  const [openProfilePopup, setOpenProfilePopup] = useState<boolean>(false);
  const [showRegisterDialog, setShowRegisterDialog] = useState<boolean>(false);
  const [showLoginDialog, setShowLoginDialog] = useState<boolean>(false);

  useEffect(() => {
    dispatch(loadUser() as any);
  }, [dispatch]);

  useEffect(() => {
    if (user) {
      dispatch(fetchTasksByUserId(user.id) as any);
    }
  }, [dispatch, user]);

  const countTasksByStatus = (status: string) => {
    return tasks.filter(task => task.status === status).length;
  };

  const handleAddTaskClick = () => {
    setShowAddTaskForm(true);
  };

  const handleRegister = (user: User) => {
    dispatch(loginUser(user) as any);
    setShowRegisterDialog(false);
  };

  const handleLogin = (user: User) => {
    dispatch(loginUser(user) as any);
    setShowLoginDialog(false);
  };

  const handleUpdateProfilePicture = async (file: File) => {
    // Your existing logic
  };

  const handleDeleteProfilePicture = async () => {
    // Your existing logic
  };

  const handleProfileClick = () => {
    setOpenProfilePopup(true);
  };

  const handleCloseProfilePopup = () => {
    setOpenProfilePopup(false);
  };

  const handleLogout = () => {
    dispatch(logoutUser());
  };

  return (
    <Router>
      <div className="professional-background left"></div>
      <div className="professional-background right"></div>
      <Container maxWidth="md" className="content-container">
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              Task Management App
            </Typography>
            <Box display="flex" alignItems="center">
              {user ? (
                <>
                  <Button onClick={handleProfileClick} sx={{ textTransform: 'none' }}>
                    <Avatar alt={user.username || 'User'} src={user.profilePictureFileName || ''} />
                  </Button>
                  <Button color="inherit" onClick={handleLogout}>Logout</Button>
                </>
              ) : (
                <>
                  <Button color="inherit" onClick={() => setShowRegisterDialog(true)}>Register</Button>
                  <Button color="inherit" onClick={() => setShowLoginDialog(true)}>Login</Button>
                </>
              )}
            </Box>
          </Toolbar>
        </AppBar>
        <main>
          <Box className="task-counters" sx={{ marginTop: 2, padding: 2, backgroundColor: '#f8f9fa', borderRadius: 2 }}>
            <Typography variant="h5" gutterBottom>Task Status</Typography>
            <Box display="flex" justifyContent="center">
              <Box display="flex" alignItems="center" mx={2}>
                <span className="status-key pending"></span>
                <Typography variant="body1" sx={{ marginLeft: 1 }}>Pending: {countTasksByStatus('Pending')}</Typography>
              </Box>
              <Box display="flex" alignItems="center" mx={2}>
                <span className="status-key in-progress"></span>
                <Typography variant="body1" sx={{ marginLeft: 1 }}>In Progress: {countTasksByStatus('InProgress')}</Typography>
              </Box>
              <Box display="flex" alignItems="center" mx={2}>
                <span className="status-key completed"></span>
                <Typography variant="body1" sx={{ marginLeft: 1 }}>Completed: {countTasksByStatus('Completed')}</Typography>
              </Box>
            </Box>
          </Box>
          <Box display="flex" justifyContent="center" sx={{ marginTop: 2 }}>
            {user && (
              <Button variant="contained" color="primary" onClick={handleAddTaskClick}>
                Add New Task
              </Button>
            )}
          </Box>
          <TaskList tasks={tasks} loadTasks={() => dispatch(fetchTasksByUserId(user?.id || 0) as any)} userId={user?.id || 0} />
          {showAddTaskForm && (
            <AddTaskForm 
              onTaskAdded={() => dispatch(fetchTasksByUserId(user?.id || 0) as any)} 
              onClose={() => setShowAddTaskForm(false)} 
              editingTask={null}
              userId={user?.id || 0}
            />
          )}
          {user && (
            <UserProfilePopup
              user={user}
              open={openProfilePopup}
              onClose={handleCloseProfilePopup}
              onUpdateProfilePicture={handleUpdateProfilePicture}
              onDeleteProfilePicture={handleDeleteProfilePicture}
            />
          )}
          <Routes>
            <Route path="/" element={<div />} /> {/* Default route */}
            <Route path="/register" element={
              <Dialog open={showRegisterDialog} onClose={() => setShowRegisterDialog(false)}>
                <DialogTitle>Register</DialogTitle>
                <DialogContent>
                  <RegisterForm onRegister={handleRegister} />
                </DialogContent>
                <DialogActions>
                  <Button onClick={() => setShowRegisterDialog(false)} color="primary">Close</Button>
                </DialogActions>
              </Dialog>
            } />
            <Route path="/login" element={
              <Dialog open={showLoginDialog} onClose={() => setShowLoginDialog(false)}>
                <DialogTitle>Login</DialogTitle>
                <DialogContent>
                  <Login onLogin={handleLogin} />
                </DialogContent>
                <DialogActions>
                  <Button onClick={() => setShowLoginDialog(false)} color="primary">Close</Button>
                </DialogActions>
              </Dialog>
            } />
          </Routes>
        </main>
      </Container>
    </Router>
  );
};

export default App;
