import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';
import { registerUser } from '../services/api';
import { User } from '../interfaces/User';
import '../forms.css'; // Import the CSS file

interface RegisterFormProps {
    onRegister: (user: User) => void;
}

const RegisterForm: React.FC<RegisterFormProps> = ({ onRegister }) => {
    const [username, setUsername] = useState<string>('');
    const [email, setEmail] = useState<string>('');
    const [passwordHash, setPasswordHash] = useState<string>('');
    const [profilePictureFileName, setProfilePictureFileName] = useState<string>('');
    const [error, setError] = useState<string | null>(null);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const response = await registerUser({ username, email, passwordHash, profilePictureFileName });
            onRegister(response.data);
        } catch (error) {
            setError('Registration failed. Please try again.');
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ marginTop: 4 }}>
            <Typography variant="h6">Register</Typography>
            {error && <Typography color="error">{error}</Typography>}
            <TextField
                label="Username"
                variant="outlined"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Email"
                variant="outlined"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Password"
                type="password"
                variant="outlined"
                value={passwordHash}
                onChange={(e) => setPasswordHash(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Profile Picture File Name"
                variant="outlined"
                value={profilePictureFileName}
                onChange={(e) => setProfilePictureFileName(e.target.value)}
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <Button type="submit" variant="contained" color="primary">
                Register
            </Button>
        </Box>
    );
};

export default RegisterForm;
