import axios from 'axios';
import { User } from '../interfaces/User';

const API_URL = 'https://localhost:7244/api/users';

const getUser = async (): Promise<User> => {
  const response = await axios.get(`${API_URL}/current`);
  return response.data;
};

const updateProfilePicture = async (file: File): Promise<void> => {
  const formData = new FormData();
  formData.append('profilePicture', file);

  await axios.post(`${API_URL}/updateProfilePicture`, formData, {
    headers: {
      'Content-Type': 'multipart/form-data'
    }
  });
};

const deleteProfilePicture = async (): Promise<void> => {
  await axios.delete(`${API_URL}/deleteProfilePicture`);
};

const userService = {
  getUser,
  updateProfilePicture,
  deleteProfilePicture,
};

export default userService;
