import React from 'react';
import { Task } from '../interfaces/Task';
import taskService from '../services/taskService';
import '../forms.css'; // Import the CSS file

interface TaskListProps {
    tasks: Task[];
    loadTasks: (userId: number) => void;
    onEditTask: (task: Task) => void;
}

const TaskList: React.FC<TaskListProps> = ({ tasks, loadTasks, onEditTask }) => {
    const handleDeleteTask = async (taskId: number) => {
        try {
            await taskService.deleteTask(taskId);
            // Assuming the userId is available somehow (e.g., from a global state or context)
            await loadTasks(1);
        } catch (error) {
            console.error('Failed to delete task:', error);
        }
    };

    return (
        <div>
            <ul className="task-list">
                {tasks.map((task) => (
                    <li key={task.id} className="task-item">
                        <div className="task-info">
                            <span className="task-title">{task.title}</span>
                            <span className="task-description">{task.description}</span>
                        </div>
                        <div className="task-status">
                            <span className={`status-key ${task.status.toLowerCase().replace(' ', '-')}`}></span>
                            {task.status}
                        </div>
                        <div className="task-actions">
                            <button className="btn btn-edit" onClick={() => onEditTask(task)}>âœŽ</button>
                            <button className="btn btn-delete" onClick={() => handleDeleteTask(task.id)}>ðŸ—‘</button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TaskList;
