import React from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Typography } from '@mui/material';
import { User } from '../interfaces/User';
import '../forms.css';

interface UserProfilePopupProps {
    user: User | null;
    open: boolean;
    onClose: () => void;
    onUpdateProfilePicture: (file: File) => void;
    onDeleteProfilePicture: () => void;
}

const UserProfilePopup: React.FC<UserProfilePopupProps> = ({ user, open, onClose, onUpdateProfilePicture, onDeleteProfilePicture }) => {
    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files && e.target.files[0];
        if (file) {
            onUpdateProfilePicture(file);
        }
    };

    return (
        <Dialog open={open} onClose={onClose}>
            <DialogTitle>User Profile</DialogTitle>
            <DialogContent>
                {user && (
                    <>
                        <Typography variant="body1">Username: {user.username}</Typography>
                        <Typography variant="body1">Email: {user.email}</Typography>
                        <Typography variant="body1">Profile Picture: {user.profilePictureFileName}</Typography>
                        <input type="file" accept="image/*" onChange={handleFileChange} />
                    </>
                )}
            </DialogContent>
            <DialogActions>
                <Button onClick={onDeleteProfilePicture} color="error">
                    Delete Profile Picture
                </Button>
                <Button onClick={onClose} color="primary">
                    Close
                </Button>
            </DialogActions>
        </Dialog>
    );
};

export default UserProfilePopup;
