import axios from 'axios';

const API_URL = 'http://localhost:7244/api/'; // Update with the correct backend URL

export interface User {
    id?: number;
    username: string;
    email?: string; // Make email optional for login
    passwordHash: string;
    profilePictureFileName?: string;
}

const registerUser = async (user: User) => {
    const response = await axios.post(`${API_URL}User/register`, user);
    return response.data;
};

const loginUser = async (username: string, passwordHash: string) => {
    const response = await axios.post(`${API_URL}User/login`, {
        username,
        passwordHash,
    });
    return response.data;
};

const getUser = async () => {
    const response = await axios.get(`${API_URL}users/current-user`);
    return response.data;
};

const updateProfilePicture = async (file: File) => {
    const formData = new FormData();
    formData.append('file', file);

    const response = await axios.post(`${API_URL}User/uploadProfilePicture`, formData, {
        headers: {
            'Content-Type': 'multipart/form-data',
        },
    });
    return response.data;
};

const deleteProfilePicture = async () => {
    const response = await axios.delete(`${API_URL}User/deleteProfilePicture`);
    return response.data;
};

export default {
    registerUser,
    loginUser,
    getUser,
    updateProfilePicture,
    deleteProfilePicture,
};
