import React, { useState } from 'react';
import { TextField, Button, Box } from '@mui/material';
import taskService from '../services/taskService';
import { Task } from '../interfaces/Task';
import '../forms.css';

interface AddTaskFormProps {
    onTaskAdded: () => void;
    onClose: () => void;
    userId: number;
    editingTask?: Task;
}

const AddTaskForm: React.FC<AddTaskFormProps> = ({ onTaskAdded, onClose, userId, editingTask }) => {
    const [title, setTitle] = useState<string>(editingTask?.title || '');
    const [description, setDescription] = useState<string>(editingTask?.description || '');
    const [status, setStatus] = useState<'Pending' | 'InProgress' | 'Completed'>(editingTask?.status || 'Pending');

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const newTask: Task = {
            id: editingTask?.id || 0,
            title,
            description,
            status,
            userId
        };

        try {
            if (editingTask) {
                await taskService.updateTask(newTask);
            } else {
                await taskService.createTask(newTask);
            }
            onTaskAdded();
            onClose();
        } catch (error) {
            console.error('Failed to add/update task:', error);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ marginTop: 4 }}>
            <TextField
                label="Title"
                variant="outlined"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Description"
                variant="outlined"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
                multiline
                rows={4}
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <Button type="submit" variant="contained" color="primary" sx={{ marginRight: 1 }}>Save Task</Button>
            <Button type="button" variant="outlined" color="secondary" onClick={onClose}>Cancel</Button>
        </Box>
    );
};

export default AddTaskForm;
