import React, { useState } from 'react';
import { TextField, Button, Box, Typography } from '@mui/material';
import { loginUser } from '../services/userService';
import './forms.css'; // Assuming you have CSS for forms

interface LoginProps {
    onLogin: (user: User) => void;
}

const Login: React.FC<LoginProps> = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isError, setIsError] = useState(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const loginData = { email, passwordHash: password }; // Ensure these fields match your backend requirements

        try {
            const response = await loginUser(loginData);
            onLogin(response); // Pass the user data to the parent component
            setMessage('Login successful!');
            setIsError(false);
        } catch (error) {
            setMessage('Login failed. Please check your credentials.');
            setIsError(true);
        }
    };

    return (
        <Box className="login-form" component="form" onSubmit={handleSubmit}>
            <Typography variant="h6">Login</Typography>
            <TextField
                label="Email"
                type="email"
                fullWidth
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                margin="normal"
            />
            <TextField
                label="Password"
                type="password"
                fullWidth
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                margin="normal"
            />
            {message && (
                <Typography color={isError ? 'error' : 'primary'}>{message}</Typography>
            )}
            <Button type="submit" variant="contained" color="primary">
                Login
            </Button>
        </Box>
    );
};

export default Login;
