import React, { useState } from 'react';
import { registerUser } from '../services/api';
import { TextField, Button, Box, Typography } from '@mui/material';
import { User } from '../interfaces/User';

interface RegisterFormProps {
    onRegister: (user: User) => void;
}

const RegisterForm: React.FC<RegisterFormProps> = ({ onRegister }) => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [passwordHash, setPasswordHash] = useState('');
    const [message, setMessage] = useState('');
    const [isError, setIsError] = useState(false);

    const handleSubmit = async (event: React.FormEvent) => {
        event.preventDefault();
        const userData: Partial<User> = { username, email, passwordHash };

        try {
            const response = await registerUser(userData as User); // Cast to User
            onRegister(response); // Pass the user data to the parent component
            setMessage('Registration successful!');
            setIsError(false);
        } catch (error) {
            setMessage('Registration failed. Please try again.');
            setIsError(true);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
            <Typography variant="h6">Register</Typography>
            <TextField
                label="Username"
                variant="outlined"
                fullWidth
                margin="normal"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <TextField
                label="Email"
                variant="outlined"
                fullWidth
                margin="normal"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
            />
            <TextField
                label="Password"
                variant="outlined"
                type="password"
                fullWidth
                margin="normal"
                value={passwordHash}
                onChange={(e) => setPasswordHash(e.target.value)}
            />
            <Button type="submit" variant="contained" color="primary" fullWidth>
                Register
            </Button>
            {message && (
                <Typography variant="body2" color={isError ? 'error' : 'primary'} sx={{ mt: 2 }}>
                    {message}
                </Typography>
            )}
        </Box>
    );
};

export default RegisterForm;
