import axios from 'axios';
import { getCurrentUser, registerUser, loginUser } from './api';
import { User } from '../interfaces/User';

const getUser = async (): Promise<User> => {
    const response = await getCurrentUser();
    return response.data;
};

const updateProfilePicture = async (file: File): Promise<void> => {
    const formData = new FormData();
    formData.append('file', file);
    await axios.post('/users/profile-picture', formData);
};

const deleteProfilePicture = async (): Promise<void> => {
    await axios.delete('/users/profile-picture');
};

const register = async (user: User): Promise<User> => {
    const completeUser: User = {
        ...user,
        profilePictureFileName: user.profilePictureFileName || ''
    };
    const response = await registerUser(completeUser);
    return response;
};

const login = async (credentials: { username: string; passwordHash: string }): Promise<User> => {
    const response = await loginUser(credentials);
    return response;
};

export default {
    getUser,
    updateProfilePicture,
    deleteProfilePicture,
    register,
    login
};
