import React, { useState } from 'react';
import { TextField, Button, Box, Typography } from '@mui/material';
import { registerUser } from '../services/api';
import { User } from '../interfaces/User';

interface RegisterFormProps {
    onRegister: (user: User) => void;
}

const RegisterForm: React.FC<RegisterFormProps> = ({ onRegister }) => {
    const [username, setUsername] = useState<string>('');
    const [email, setEmail] = useState<string>('');
    const [password, setPassword] = useState<string>('');
    const [profilePictureFileName, setProfilePictureFileName] = useState<string>('');
    const [message, setMessage] = useState<string>('');
    const [isError, setIsError] = useState<boolean>(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const registerData: User = {
            id: 0, // Default value, as it's not needed for registration
            username,
            email,
            passwordHash: password,
            profilePictureFileName
        };
        try {
            const response = await registerUser(registerData);
            onRegister(response); // Pass the user data to the parent component
            setMessage('Registration successful!');
            setIsError(false);
        } catch (error) {
            setMessage('Registration failed.');
            setIsError(true);
        }
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ marginTop: 4 }}>
            <Typography variant="h6">Register</Typography>
            <TextField
                label="Username"
                variant="outlined"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Email"
                variant="outlined"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Password"
                variant="outlined"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <TextField
                label="Profile Picture File Name"
                variant="outlined"
                value={profilePictureFileName}
                onChange={(e) => setProfilePictureFileName(e.target.value)}
                fullWidth
                sx={{ marginBottom: 2 }}
            />
            <Button type="submit" variant="contained" color="primary" sx={{ marginRight: 1 }}>
                Register
            </Button>
            {message && (
                <Typography variant="body1" color={isError ? 'error' : 'primary'}>
                    {message}
                </Typography>
            )}
        </Box>
    );
};

export default RegisterForm;
