import axios from 'axios';
import { Task } from '../interfaces/Task';

const API_BASE_URL = 'https://localhost:7244/api';

const createTask = async (task: Task) => {
    const response = await axios.post(`${API_BASE_URL}/tasks`, task);
    return response.data;
};

const updateTask = async (task: Task) => {
    const response = await axios.put(`${API_BASE_URL}/tasks/${task.id}`, task);
    return response.data;
};

const deleteTask = async (taskId: number) => {
    const response = await axios.delete(`${API_BASE_URL}/tasks/${taskId}`);
    return response.data;
};

export const getTasksByUserId = async (userId: number) => {
    const response = await axios.get(`${API_BASE_URL}/tasks/user/${userId}`);
    return response.data;
};

export default {
    createTask,
    updateTask,
    deleteTask,
    getTasksByUserId,
};
