import React, { useState } from 'react';
import { List, ListItem, ListItemText, TextField, Select, MenuItem, IconButton, Box, Button } from '@mui/material';
import { Task } from '../interfaces/Task';
import taskService from '../services/taskService';
import '../styles.css';

interface TaskListProps {
  tasks: Task[];
  loadTasks: () => void;
}

const TaskList: React.FC<TaskListProps> = ({ tasks, loadTasks }) => {
  const [editingTaskId, setEditingTaskId] = useState<number | null>(null);
  const [editedTask, setEditedTask] = useState<Partial<Task>>({});

  const handleEditClick = (task: Task) => {
    setEditingTaskId(task.id);
    setEditedTask(task);
  };

  const handleSaveClick = async (task: Task) => {
    try {
      await taskService.updateTask({ ...task, ...editedTask });
      setEditingTaskId(null);
      loadTasks();
    } catch (error) {
      console.error('Failed to update task:', error);
    }
  };

  const handleCancelClick = () => {
    setEditingTaskId(null);
    setEditedTask({});
  };

  const handleChange = (field: keyof Task, value: any) => {
    setEditedTask((prev) => ({ ...prev, [field]: value }));
  };

  return (
    <List>
      {tasks.map((task) => (
        <ListItem key={task.id} className="task-item">
          <Box display="flex" alignItems="center" width="100%">
            {editingTaskId === task.id ? (
              <>
                <TextField
                  label="Title"
                  variant="outlined"
                  value={editedTask.title ?? task.title}
                  onChange={(e) => handleChange('title', e.target.value)}
                  className="form-field"
                />
                <TextField
                  label="Description"
                  variant="outlined"
                  value={editedTask.description ?? task.description}
                  onChange={(e) => handleChange('description', e.target.value)}
                  className="form-field"
                />
                <Select
                  value={editedTask.status ?? task.status}
                  onChange={(e) => handleChange('status', e.target.value)}
                  className="form-field"
                >
                  <MenuItem value="Pending">Pending</MenuItem>
                  <MenuItem value="InProgress">In Progress</MenuItem>
                  <MenuItem value="Completed">Completed</MenuItem>
                </Select>
                <Button onClick={() => handleSaveClick(task)} variant="contained" color="primary" className="form-button">Save</Button>
                <Button onClick={handleCancelClick} variant="outlined" color="secondary" className="form-button">Cancel</Button>
              </>
            ) : (
              <>
                <ListItemText primary={task.title} secondary={task.description} />
                <Box display="flex" alignItems="center">
                  <span className={`status-key ${task.status.toLowerCase()}`}></span>
                  <span>{task.status}</span>
                  <IconButton onClick={() => handleEditClick(task)}>‚úèÔ∏è</IconButton>
                  <IconButton onClick={() => taskService.deleteTask(task.id).then(loadTasks)}>üóëÔ∏è</IconButton>
                </Box>
              </>
            )}
          </Box>
        </ListItem>
      ))}
    </List>
  );
};

export default TaskList;
