import React, { useState, useEffect } from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Avatar, Box, Typography } from '@mui/material';
import { User } from '../interfaces/User';
import userService from '../services/userService';
import './UserProfilePopup.css';

interface UserProfilePopupProps {
  user: User;
  open: boolean;
  onClose: () => void;
  onUpdateProfilePicture: () => void;
  onDeleteProfilePicture: () => void;
}

const UserProfilePopup: React.FC<UserProfilePopupProps> = ({ user, open, onClose, onUpdateProfilePicture, onDeleteProfilePicture }) => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [profilePicture, setProfilePicture] = useState<string | null>(null);
  const [previewPicture, setPreviewPicture] = useState<string | null>(null);

  useEffect(() => {
    const loadProfilePicture = async () => {
      if (user.id) {
        try {
          const picture = await userService.getProfilePicture(user.id);
          setProfilePicture(picture);
        } catch (error) {
          console.error('Failed to load profile picture:', error);
        }
      }
    };

    loadProfilePicture();
  }, [user]);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files.length > 0) {
      const file = event.target.files[0];
      setSelectedFile(file);
      setPreviewPicture(URL.createObjectURL(file));
    }
  };

  const handleUploadClick = async () => {
    if (selectedFile) {
      try {
        await userService.uploadProfilePicture(user.id, selectedFile);
        onUpdateProfilePicture();
        setSelectedFile(null);
        setPreviewPicture(null);
        onClose();
      } catch (error) {
        console.error('Failed to update profile picture:', error);
      }
    }
  };

  const handleUpdateClick = async () => {
    if (selectedFile) {
      try {
        await userService.updateProfilePicture(user.id, selectedFile);
        onUpdateProfilePicture();
        setSelectedFile(null);
        setPreviewPicture(null);
        onClose();
      } catch (error) {
        console.error('Failed to update profile picture:', error);
      }
    }
  };

  const handleDeleteClick = async () => {
    try {
      await userService.deleteProfilePicture(user.id);
      onDeleteProfilePicture();
      onClose();
    } catch (error) {
      console.error('Failed to delete profile picture:', error);
    }
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Profile</DialogTitle>
      <DialogContent>
        <Box className="profile-popup-container">
          <Avatar alt={user.username} src={previewPicture || profilePicture || ''} className="profile-avatar" />
          <Typography className="profile-username">{user.username}</Typography>
          <Typography className="profile-email">{user.email}</Typography>
          <Box className="file-input-container">
            <input
              id="file-input"
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              className="hidden-file-input"
            />
            <label htmlFor="file-input">
              <Button variant="contained" component="span" className="file-input-label">
                Choose File
              </Button>
            </label>
            {profilePicture ? (
              <Button
                variant="contained"
                color="primary"
                onClick={handleUpdateClick}
                className="upload-button"
              >
                Update
              </Button>
            ) : (
              <Button
                variant="contained"
                color="primary"
                onClick={handleUploadClick}
                className="upload-button"
              >
                Upload
              </Button>
            )}
          </Box>
        </Box>
      </DialogContent>
      <DialogActions className="dialog-actions">
        <Button onClick={onClose} color="primary" className="button-close">Close</Button>
        <Button onClick={handleDeleteClick} color="secondary" className="button-delete">Delete Profile Picture</Button>
      </DialogActions>
    </Dialog>
  );
};

export default UserProfilePopup;
